import React, { useState, useEffect } from 'react'
import apiServer from '../services/apiService'

export default function User(props) {
    const [myUsers, setMyUsers] = useState(null)
    const [index, setIndex] = useState(null);
    const [selectedUser, setSelectedUser] = useState(null);

    useEffect(() => {
        let myUserPath = window.location.pathname.slice(6)
        apiServer.then(res => { setMyUsers(res) })
        myUsers && myUsers.forEach((element, i) => {
            let selectedFullName = element.name.first + element.name.last;
            selectedFullName.toString() == myUserPath.toString() && setIndex(i);
        })
        index != null && setSelectedUser(myUsers[index])
    }, [myUsers, index])

    return (
        <div>
            {selectedUser != null && <React.Fragment>
                <h3><u>Name</u> {selectedUser.name.first} {selectedUser.name.last}</h3>
                <h3><u>Age</u> {selectedUser.age}</h3>
                <h3><u>Company</u>: {selectedUser.company}</h3>
                <h3><u>Email</u>: {selectedUser.email}</h3>
                <h3><u>Phone</u>: {selectedUser.phone}</h3>
                <h3><u>Address</u>: {selectedUser.address}</h3>
                <h3><u>Favorite Fruit</u>: {selectedUser.favoriteFruit}</h3>
                <h3><u>About</u>: <p>{selectedUser.about}</p></h3>
                <h3 className="greeting">{selectedUser.greeting}</h3>
            </React.Fragment>}
        </div>
    )
}
