import React, { useState ,useEffect} from 'react'
import apiServer from '../services/apiService'

export default function User(props) {
    const [myUsers, setMyUsers] = useState(null)
    const [checker, setChecker] = useState(false);
    const [index, setIndex] = useState(3);
    apiServer.then(res => {setMyUsers(res) })
    console.log(myUsers)

   useEffect(() => {
    apiServer.then(res => {setMyUsers(res) })
    let myUserPath = window.location.pathname.slice(6)
    console.log('asd')
    myUsers && myUsers.forEach((element,index) => {
        let selectedFullName = element.name.first + element.name.last;
        selectedFullName.toString() == myUserPath.toString() && setIndex(index);
    })
   }, [])

    const item = myUsers[index];
    console.log(item)
    return (
        <div>

            {item ?  <React.Fragment>
                <h3><u>Name</u> {item.name.first} {item.name.last}</h3>
                <h3><u>Age</u> {item.age}</h3>
                <h3><u>Company</u>: {item.company}</h3>
                <h3><u>Email</u>: {item.email}</h3>
                <h3><u>Phone</u>: {item.phone}</h3>
                <h3><u>Address</u>: {item.address}</h3>
                <h3><u>Favorite Fruit</u>: {item.favoriteFruit}</h3>
                <h3><u>About</u>: <p>{item.about}</p></h3>
                <h3 className="greeting">{item.greeting}</h3>
                </React.Fragment> : <h1>N/A try to log in again</h1> }
        </div>
    )
}
