import React, {createContext, useState }from 'react'
import RegisterPage from './components/pages/RegisterPage'
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import UsersPage from './components/pages/Users';
import User from './components/pages/User';
import LoginPage from './components/pages/LoginPage';
import apiService from './components/services/apiService';
import Footer from './components/Footer'

export const userContext = createContext();
export default function RoutePages() {
    const [isLoggedIn ,setIsLoggedIn] = useState(false);
    const [userName ,setUserName] = useState('');
    const [specificUserId, setSpecificUserId] = useState('');
    const [specificUserObj, setSpecificUserObj] = useState(null);
    const myObjects = async ()=> await apiService.then(res => { setMyUsers(res) })
    function setLogged(name){
        setUserName(name)
        setIsLoggedIn(true);
    }

    function getUser(id,obj){
        setSpecificUserId(id);
        setSpecificUserObj(obj)
    }
    return (
        <Router >
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
            <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarText" aria-controls="navbarText" aria-expanded="false" aria-label="Toggle navigation">
                <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarText">
                <ul className="navbar-nav nav-pills nav-fill">
                    <li className="nav-item px-4 m-2 nav-link">
                    <Link className='nav-link' to="/">Users</Link>
                    </li>
                    {isLoggedIn ? <li className="nav-item px-4 m-2 nav-link">
                    <a  className='nav-link' href="/login" onClick={()=>{setIsLoggedIn(false)}}>Logout</a>
                    </li>:
                     <li className="nav-item px-4 m-2 nav-link">
                    <Link className='nav-link' to="/login">Login</Link>
                    </li>}

                    <li className="nav-item px-4 m-2 nav-link ">
                    <Link className='nav-link' to="/register">Register</Link>
                    </li>
                    <li className="nav-item px-4 m-2 nav-link ">
                        <h2>{isLoggedIn ? 'Welcome ' + `${userName}` : '' }</h2>
                    </li>
                </ul>
            </div>
        </nav>
        <Switch>
            <Route component={LoginPage} path="/login" >
            <LoginPage success = {setLogged}/>
            </Route>
            <Route path="/register">
                <RegisterPage />
            </Route>
            <Route path= {`/user/${specificUserId}`}>
                <User userObject = {specificUserObj}/>
            </Route>
            <Route path="/">
                <UsersPage isLogged = {isLoggedIn} getUser ={getUser}/>
            </Route>
        </Switch>
        {/* <Footer/> */}
</Router>


    )
}
