import React, { useState, useEffect } from 'react'
import apiServer from '../services/apiService'

export default function User(props) {
    const [myUsers, setMyUsers] = useState(null)
    const [index, setIndex] = useState(null);

    useEffect(() => {
        let myUserPath = window.location.pathname.slice(6)
        apiServer.then(res => { setMyUsers(res) })
        myUsers && myUsers.forEach((element, i) => {
            let selectedFullName = element.name.first + element.name.last;
            selectedFullName.toString() == myUserPath.toString() && setIndex(i);
        })
    }, [myUsers, index])

    return (
        <div>
            {index != null && <React.Fragment>
                <h3><u>Name</u> {myUsers[index].name.first} {myUsers[index].name.last}</h3>
                <h3><u>Age</u> {myUsers[index].age}</h3>
                <h3><u>Company</u>: {myUsers[index].company}</h3>
                <h3><u>Email</u>: {myUsers[index].email}</h3>
                <h3><u>Phone</u>: {myUsers[index].phone}</h3>
                <h3><u>Address</u>: {myUsers[index].address}</h3>
                <h3><u>Favorite Fruit</u>: {myUsers[index].favoriteFruit}</h3>
                <h3><u>About</u>: <p>{myUsers[index].about}</p></h3>
                <h3 className="greeting">{myUsers[index].greeting}</h3>
            </React.Fragment>}
        </div>
    )
}
