import React, { useState,useContext } from 'react'
import { Button, TextField } from '@material-ui/core';
import { useHistory } from "react-router-dom";
import {userContext} from '../../RoutePages'



export default function LoginPage(props) {
    const [message, setMessage] = useState('')
    const {setLoginNLogout} = useContext(userContext);
    const history = useHistory();
    const txtNBtnStyle = { margin: '10px', width: '200px' }

    function checkIdAndPassword() {
        let password = document.getElementById('passwordId')
        let id = document.getElementById('userNameId')
        if (localStorage.getItem(id.value) !== null && localStorage.getItem(id.value) === password.value) {
            localStorage.setItem('selectedUser', id.value)
            localStorage.setItem('isLogged','true')
            setLoginNLogout('Logout')
            history.push('/users')
        } else {
            setMessage('Failed, Please Try Again!')
        }
    }
    return (
        <div>
            <div style={{display: 'flex', justifyContent:"flex-end", margin:'20px 20px'}}>
                <img src="./login.svg" alt="" width='700px' />
            </div>
            <div style={{}}>
                <div style={{ }}>
                    <TextField id ='userNameId' style={txtNBtnStyle} label="Enter UserName" variant="outlined" type="text" /><br />
                    <TextField  id ='passwordId'  style={txtNBtnStyle} label="Enter Password"  variant="outlined" type="text" placeholder="Enter Password" />
                    <h3 style={{ color: 'red' }}>{message}</h3>
                </div>
                <div style={{}}>
                    <Button style={txtNBtnStyle} variant="contained" color="primary" onClick={checkIdAndPassword}>Log In</Button>
                    <Button style={txtNBtnStyle} variant="contained" color="primary" onClick={() => { history.push('/register') }}>Sign Up</Button>
                </div>
            </div>
        </div>

    )
}
