import React, { useState } from 'react'
import { Button, TextField } from '@material-ui/core';
import { useHistory } from "react-router-dom";


export default function LoginPage(props) {
    const [message, setMessage] = useState('')
    const history = useHistory();
    const [id, setId] = useState('')
    const [password, setPassword] = useState('')
    const txtNBtnStyle = { margin: '10px', width: '200px' }
    function checkIdAndPassword() {
        if (localStorage.getItem(id) !== null && localStorage.getItem(id) === password) {
            props.success(localStorage.getItem(id));
            history.push('/users')
        } else {
            setMessage('Failed, Please Try Again!')
        }
    }
    return (
        <div>
            {/* SVG div */}
            <div style={{ position: 'absolute', top: '13%', left: '50%' }}>
                <img src="./login.svg" alt="" width='700px' />
            </div>
            <div style={{ position: "absolute", top: '60%', left: '25%', transform: 'translate(-50%, -50%)' }}>
                <div style={{ position: "relative", left: '25%', marginBottom: '30px' }}>
                    <TextField style={txtNBtnStyle} label="Enter UserName" onChange={(e) => { setId(e.target.value) }} variant="outlined" type="text" /><br />
                    <TextField style={txtNBtnStyle} label="Enter Password" onChange={(e) => { setPassword(e.target.value) }} variant="outlined" type="text" placeholder="Enter Password" />
                    <h3 style={{ color: 'red' }}>{message}</h3>
                </div>
                <div style={{ display: 'flex' }}>
                    <Button style={txtNBtnStyle} variant="contained" color="primary" onClick={checkIdAndPassword}>Log In</Button>
                    <Button style={txtNBtnStyle} variant="contained" color="primary" onClick={() => { history.push('/register') }}>Sign Up</Button>
                </div>
            </div>
        </div>

    )
}
