import React from 'react'

export default function Card(props) {

    function clickHandle(){
        props.getUser(props.name)

    }

    return (
            <div className = 'col-3 border border-dark py-5 m-2 text-center' style={{height:'400px'}} onClick={clickHandle}>
                <h4 className='pt-4'><b>Full name: </b>{props.name}</h4>
                <h5 className='pt-4'><b>age: </b>{props.age}</h5>
                <h6 className='pt-4'><b>email: </b>{props.email}</h6>
                <img className='pt-5' src={props.picture} width="100px" alt=""/>
            </div>

    )
}
